INSERT INTO `wp_bp_activity` VALUES (22, 3, 'blogs', 'new_blog_post', '<a href="http://web3.cmmt.ubc.ca/dev/members/editors/" title="Editors">Editors</a> wrote a new blog post: <a href="http://www.news-medical.net/news/2004/07/31/3691.aspx">Twin research reveals gene that may play a crucial part in the development of myopia</a>', 'New research carried out by doctors in the Twin Research Unit at St Thomas’ Hospital, London, indicates that a gene called PAX6 may play a crucial part in the development of myopia.', 'http://www.news-medical.net/news/2004/07/31/3691.aspx', '1', '181', '2004-07-31 05:06:00', 0, 0, 0); 
INSERT INTO `wp_bp_activity` VALUES (23, 3, 'blogs', 'new_blog_post', '<a href="http://web3.cmmt.ubc.ca/dev/members/editors/" title="Editors">Editors</a> wrote a new blog post: <a href="http://www.news-medical.net/news/2008/08/28/40994.aspx">Low levels of \'brain derived neurotrophic factor\' may lead to obesity</a>', 'A brain chemical that plays a role in long term memory also appears to be involved in regulating how much people eat and their likelihood of becoming obese, according to a National Institutes of Health study of a rare genetic condition.', 'http://www.news-medical.net/news/2008/08/28/40994.aspx', '1', '180', '2008-08-28 22:07:00', 0, 0, 0); 
INSERT INTO `wp_bp_activity` VALUES (24, 3, 'blogs', 'new_blog_post', '<a href="http://web3.cmmt.ubc.ca/dev/members/editors/" title="Editors">Editors</a> wrote a new blog post: <a href="http://www.news-medical.net/news/20090527/Stem-cell-breakthrough-restores-sight-to-sufferers-of-corneal-disease.aspx">Stem cell breakthrough restores sight to sufferers of corneal disease</a>', 'In a world-first breakthrough, University of New South Wales (UNSW) medical researchers have used stem cells cultured on a simple contact lens to restore sight to sufferers of blinding corneal disease.', 'http://www.news-medical.net/news/20090527/Stem-cell-breakthrough-restores-sight-to-sufferers-of-corneal-disease.aspx', '1', '179', '2009-05-27 19:28:00', 0, 0, 0); 
