<?php/*Template Name: Experts Page*/?><?php get_header() ?>	<div id="content">		<div class="padder">		<div class="page" id="experts-page">					<h1>Experts</h1>			<p>Disclaimer: PFOND Expert Pages are produced by volunteer editors. PFOND does not have any affiliation with the people listed on these pages. Volunteer editors typically populate these pages by searching in publicly available resources. If you are listed on a PFOND Expert Page and would like your listing removed or modified please <a href="<?php echo get_site_url(1) . '/contact-pfond/' ; ?>">contact us</a>.</p>					<?php			$params = array(				'post_type' => 'expert',				'orderby' => 'title',				'order' => 'ASC',				'nopaging' => true			);			query_posts ($params);						if (have_posts()) : while (have_posts()) : the_post();			$contact = get_post_meta(get_the_ID(), "expert-contact", true);			$website = get_post_meta(get_the_ID(), "expert-link", true);			?>							<div class="post" id="expert-<?php the_ID(); ?>">					<h2 class="posttitle"><a href="<?php the_permalink() ?>" rel="bookmark" title="<?php _e( 'Permanent Link to', 'buddypress' ) ?> <?php the_title_attribute(); ?>"><?php the_title(); ?></a></h2>										<div class="expertmetadata">						<!-- <div class="portrait"></div> -->						Contact: <?php						if ($contact):							echo '<a href="mailto:' . $contact .'">' . $contact . '</a><br />';						else:							echo 'N/A<br />';						endif; ?>						Website: <?php						if ($website):							echo '<a href="' . $website .'" target="_blank">' . $website . '</a><br />';						else:							echo 'N/A<br />';						endif; ?>						<?php pfond_get_terms(get_the_ID(), "Location", "location") ?><br />						<?php pfond_get_terms(get_the_ID(), "Specializations", "specialization") ?>					</div>										<?php the_content(); ?>									</div>						<?php endwhile; endif; // endforeach; ?>					</div><!-- .page -->						</div><!-- .padder -->	</div><!-- #content -->		<?php locate_template( array( 'sidebar.php' ), true ) ?><?php get_footer(); ?>